import './theme/styles.css';
import {
  BrowserRouter,
  Route,
  useParams,
  Routes
} from "react-router-dom";
import Home from './screens/Home';
import Books from './screens/Books';
import Authors from './screens/Authors';
import Addauthors from './screens/AddAuthors';
import BookDetails from './screens/BookDetails';
import { Login } from './screens/Login';
import { useEffect, useState } from 'react';
import { Sidebar } from './components/Sidebar';
import Categories from './screens/Categories';


function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const logOut = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    setToken(undefined);
  }
  const pullTokenFromLogin = (token) => {
    setToken(token);
  }
  const login = (<Login setAppToken={pullTokenFromLogin} />);
  const noAuthRoutes = (
    <div className="container">
    <BrowserRouter>
      <Sidebar auth={false}/>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="books" element={<Books />} />
          <Route path="books/:bookId" element={<BookDetails />} />
          <Route path="authors" element={<Authors />} />
          <Route path="categories" element={<Categories />} />
          <Route path="login" element={login} />
        </Routes>
    </BrowserRouter>
  </div>
  )
  const protectedRoutes = (
    <div className="container">
    <BrowserRouter>
      <Sidebar auth={true} />
      <button style={{"margin":"2rem"}} onClick={logOut}>Log out!</button>
      <div id='content'>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="books" element={<Books />} />
          <Route path="books/:bookId" element={<BookDetails />} />
          <Route path="authors" element={<Authors />} />
          <Route path="admin/categories" element={<Categories />} />
          <Route path="admin/add-authors" element={<Addauthors />} />
        </Routes>
      </div>
    </BrowserRouter>
  </div>);
  
  if(!token) {
    return noAuthRoutes;
  }
  
  return protectedRoutes;
}


export default App;
