import Select from 'react-select';
import axios from "axios";
import { useEffect, useState } from "react";






export default function AddBooks() {

  const [authors, setAuthors] = useState([]);
  const [categories, setCategories] = useState([]);
  useEffect(() => {
    axios.get('http://localhost:3001/authors')
    .then(res => {
      if (res.status !== 200 && res.status !== 201) {
        console.log('Error!');
        throw new Error('Could not authenticate you!');
      }
      return res.data.authors;
    })
    .then((authors) => {
      setAuthors(authors);
    });


    axios.get('http://localhost:3001/categories')
    .then(res => {
      if (res.status !== 200 && res.status !== 201) {
        console.log('Error!');
        throw new Error('Could not authenticate you!');
      }
      return res.data.categories;
    })
    .then((categories) => {
      setCategories(categories);
    });
  }, []);


  let optionsAuthor = authors.map(function (author) {
    return { value: author.name, label: author.name };
  })


  let optionsCategories = categories.map(function (category) {
    return { value: category.name, label: category.name };
  })  


  function submitLogin(event) {
    event.preventDefault();
    const form = event.currentTarget;
    const title = form.querySelector('input[name="title"]');
    const author = form.querySelector('input[name="author"]');
    const description = form.querySelector('input[name="description"]');
    const categories = form.querySelector('input[name="categories"]');
    const img = form.querySelector('input[type="file"]');

    const data = {}; 
    data.title = title.value;
    data.password = password.value;

    axios.post("http://localhost:3001/login", {
      email: mail.value,
      password: password.value
    }).then(res => {
      if (res.status === 422) {
        throw new Error('Validation failed.');
      }
      if (res.status !== 200 && res.status !== 201) {
        console.log('Error!');
        throw new Error('Could not authenticate you!');
      }
      return res.data;
    }).then(resData => {
      console.log(resData);
      localStorage.setItem("token", resData.token);
      localStorage.setItem("userId", resData.userId);
      setAppToken(resData.token);
      navigate('/admin/books', {replace: true});
    })
  } 

    return (
      
      <main >
         <form onSubmit={submitLogin} action="">
        <div className="add-materials">
          <h1>Add Book</h1>
          <input className="input-type-text" type="text"  placeholder="Type books's title" name="title"></input>
         <Select  className="input-type-select"  name="author" options={optionsAuthor} />
         <textarea  className="input-type-text" id="description" placeholder="Type books's description" name="description"></textarea>
         <Select  className="input-type-select"  isMulti name="categories" options={optionsCategories} />
         <input type="file"></input>
         
        </div>

        <div className="button-submit">
        <button type="submit">Add</button>
        </div>
        </form>
      </main>
    );
  }
