import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

export function BookAuth(props){
    const [auth, setAuth] = useState(false);
    const { id } = props;
    useEffect(() => { 
        let token = localStorage.getItem('token');
        token ? setAuth(true) : setAuth(false);
    }, []);

    const deletePost = async (e) => {
        e.preventDefault();
        try {
          await axios.delete(`${API_ENDPOINT}/${readPostId}`);
         
        } catch (err) {
          writeStatus("Post deletion failed");
        }
      };

    useEffect(() => {
    
        async function deletePost() {
            await axios.delete('https://localhost:3001/admin/books' + id);
        }
    
        deletePost();
    }, [id]);

    if(auth) {
        return(
            <div>
                 <form onSubmit={deletePost}>
                 <button type="submit">Delete</button>
                 </form>
                
                <button>Edit</button>
            </div>
        );
    }
    else{
        return(
            <div>
                 <button className="btn"><Link to={'/books/' + id} >Details</Link></button>
            </div>
        );
    }
  
}
