import Select from 'react-select'

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' }
]

export default function Addauthors() {
    return (
      <main >
        <div className="add-materials">
        <input className="input-type-text" type="text" name="title"></input>
         <Select  className="input-type-select" name="author" options={options} />
         <textarea  className="input-type-text" name="description"></textarea>
         <Select  name="categories" options={options} />
         <input type="file"></input>
        </div>
      
      </main>
    );
  }
