import Select from 'react-select';
import axios from "axios";
import { useEffect, useState } from "react";






export default function AddBooks() {

  const [authors, setAuthors] = useState([]);
  useEffect(() => {
    axios.get('http://localhost:3001/authors')
    .then(res => {
      if (res.status !== 200 && res.status !== 201) {
        console.log('Error!');
        throw new Error('Could not authenticate you!');
      }
      return res.data.authors;
    })
    .then((authors) => {
      setAuthors(authors);
    });
  }, []);


  // const Authors = authors ? (
  //   authors.map((author) => {
  //       return author.name
  //   })
  //      ) : {};


  //      const options = [
  //       {  }
  //     ]
      


  let options = authors.map(function (author) {
    return author.name;
  })
  


    return (
      
      <main >
        <div className="add-materials">
          <h1>Add Book</h1>
        <input className="input-type-text" type="text" name="title"></input>
         <Select  className="input-type-select" name="author" options={options} />
         <textarea  className="input-type-text" name="description"></textarea>
         <Select  className="input-type-select" name="categories" options={options} />
         <input type="file"></input>
        </div>
        <div className="button-submit">
        <button type="submit">Add</button>
        </div>
      
      </main>
    );
  }
