import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

export function BookAuth(props){
    const [auth, setAuth] = useState(false);
    const { id } = props;
    useEffect(() => { 
        let token = localStorage.getItem('token');
        token ? setAuth(true) : setAuth(false);
    }, []);



    // useEffect(() => {
    
    //     async function deletePost() {
    //         await axios.delete('https://localhost:3001/admin/books' + id);
    //     }
    
    //     deletePost();
    // }, [id]);

    const deleteRequestHandler = () => {
        axios.delete(
          (`https://localhost:3001/admin/books/` + id)
        );
      };


      async function deleteDataById() {
    
        if (id) {
          try {
            const res = await axios.delete(`https://localhost:3001/admin/books/` + id);
    
            const result = {
              data: res.data,
            };
          } catch (err) {
            console.log(err);
          }
        }
      }
      
      

    if(auth) {
        return(
            <div>
                 <form onSubmit={deleteRequestHandler}>
                 <button type="submit">Delete</button>
                 </form> 
                
                <button>Edit</button>
            </div>
        );
    }
    else{
        return(
            <div>
                 <button className="btn"><Link to={'/books/' + id} >Details</Link></button>
            </div>
        );
    }
  
}
