import { Link } from "react-router-dom";
import { withRouter } from "react-router"
import BookDetails from "../../screens/BookDetails";

export function Bookcard(props) {
    const { bookData } = props;
    const img = bookData.imageUrl ? (<img src={bookData.imageUrl} alt="Book Cover"></img>) : {};
    const title = bookData.title ? (<h1>{bookData.title}</h1>) : {};
    const author = bookData.author ? (<p className="author">{bookData.author}</p>) : {};
    const description = bookData.description ? (<p className="description">{bookData.description}</p>) : {};
    const categories = bookData.categories ? (
        bookData.categories.map((category) => {
            return (<span className="category">{category} </span>)
        })
    ) : {};
    return (
        <div data-id={bookData._id} className="book-card">
            <div className="book-image">
                {img}
            </div>
            <div className="book-info">
                {title}
                {author}
                {description}
                {categories}
            </div>
            <div>
            <button><Link to={'/books/' + bookData._id} >Details</Link></button>
            </div>


            <div class="component">
  <ul class="align">
  
    <li>
      <figure class='book'>        
       
        <ul class='hardcover_front'>
          <li>
          <img src={bookData.imageUrl} alt="Book Cover" width="100%" height="100%"></img>
            <span class="ribbon bestseller">NÂº1</span>
          </li>
          <li></li>
        </ul>        
          
        <ul class='page'>
          <li></li>
          <li>
          <button className><Link to={'/books/' + bookData._id} >Details</Link></button>
            <a class="btn" href="#">Download</a>
          </li>
          <li></li>
          <li></li>
          <li></li>
        </ul>        
        
        <ul class='hardcover_back'>
          <li></li>
          <li></li>
        </ul>
        <ul class='book_spine'>
          <li></li>
          <li></li>
        </ul>
        <figcaption>
          <h1>Responsive Web Design</h1>
          <span>By Ethan Marcotte</span>
          <p>From mobile browsers to netbooks and tablets, users are visiting your sites from an increasing array of devices and browsers. Are your designs ready?...</p>
        </figcaption>
      </figure>
    </li>  
  </ul>
        </div>
           
        </div>




    )
}
