import { Link } from "react-router-dom";
import { withRouter } from "react-router"
import BookDetails from "../../screens/BookDetails";

export function Bookcard(props) {
    const { bookData } = props;
    const img = bookData.imageUrl ? (<img src={bookData.imageUrl} alt="Book Cover"></img>) : {};
    const title = bookData.title ? (<h1>{bookData.title}</h1>) : {};
    const author = bookData.author ? (<p className="author">{bookData.author}</p>) : {};
    const description = bookData.description ? (<p className="description">{bookData.description}</p>) : {};
    const categories = bookData.categories ? (
        bookData.categories.map((category) => {
            return (<span className="category">{category} </span>)
        })
    ) : {};
    return (
        <div data-id={bookData._id} >
            <div class="component"  data-id={bookData._id}>
     <ul class="align">
  
       <li>
      <figure class='book'>        
       
        <ul class='hardcover_front'>
          <li>
          <img src={bookData.imageUrl} alt="Book Cover" width="100%" height="100%"></img>
            <span class="ribbon bestseller">Nº1</span>
          </li>
          <li></li>
        </ul>        
          
        <ul class='page'>
          <li></li>
          <li>
          <button className="btn"><Link to={'/books/' + bookData._id} >Details</Link></button>
          </li>
          <li></li>
          <li></li>
          <li></li>
        </ul>        
        
        <ul class='hardcover_back'>
          <li></li>
          <li></li>
        </ul>
        <ul class='book_spine'>
          <li></li>
          <li></li>
        </ul>
        <figcaption>
          <h1>{title}</h1>
          <h3>By {author}</h3>
          <p>{description}</p>
        </figcaption>
      </figure>
    </li>  
  </ul>
        </div>
           
        </div>




    )
}
